section.script:
$( document ).ready(function() {
    $.ajax({
    type: "GET",
    url: "../api/v1/search?q=objectType:mods AND state:published&rows=0&wt=json&json.wrf=?",
    dataType: "jsonp",
    success: function (data) {
    $('#project-searchInput').attr('placeholder', 'Suche in ' + $.number(data.response.numFound,0, ',', '.') +
    ' Dokumenten');
    },
    });
    });

$( document ).ready(function() {
    $.ajax({
