    url: "../api/v1/search?q=objectType:mods AND state:published&rows=0&wt=json&json.wrf=?",
    dataType: "jsonp",
    success: function (data) {
    $('#project-searchInput').attr('placeholder', 'Search in ' + $.number(data.response.numFound,0, ',', '.') +
    ' documents');
    },
    });
    });
